mapper.update:
UPDATE shopping_cart
user_id = #{userId}, dish_id = #{dishId}, setmeal_id = #{setmealId}, name = #{name}, image = #{image}, dish_flavor = #{dishFlavor}, number = #{number}, amount = #{amount}, create_time = #{createTime},
WHERE id = #{id}

mapper.select:
SELECT *
FROM shopping_cart


        AND user_id = #{userId}


        AND dish_id = #{dishId}


        AND setmeal_id = #{setmealId}


        AND dish_flavor = #{dishFlavor}
